import { db } from "./db";
import * as schema from "@shared/schema";
import { eq } from "drizzle-orm";
import bcrypt from "bcryptjs";

async function seed() {
  console.log(" Seeding database...");

  try {
    // Create admin user
    const hashedPassword = await bcrypt.hash("admin123", 10);
    const [admin] = await db.insert(schema.users).values({
      username: "admin",
      email: "admin@arcane arc.com",
      password: hashedPassword,
      fullName: "Admin User",
      role: "admin",
    }).onConflictDoNothing().returning();

    console.log(" Admin user created:", admin?.username || "already exists");

    // Create 8 weeks
    const weekData = [
      {
        weekNumber: 1,
        title: "Self Reflection",
        description: "Begin your journey with deep self-discovery and understanding your current state.",
        icon: "Target",
        color: "#FFD700",
      },
      {
        weekNumber: 2,
        title: "Skill Growth",
        description: "Identify skills you want to develop and create a learning roadmap.",
        icon: "BookOpen",
        color: "#4DD0E1",
      },
      {
        weekNumber: 3,
        title: "Physical Health",
        description: "Build sustainable fitness habits and optimize your physical well-being.",
        icon: "Dumbbell",
        color: "#7C4DFF",
      },
      {
        weekNumber: 4,
        title: "Mind & Spirit",
        description: "Cultivate mental clarity through mindfulness and spiritual practices.",
        icon: "Brain",
        color: "#AB47BC",
      },
      {
        weekNumber: 5,
        title: "Knowledge Expansion",
        description: "Broaden your horizons with new ideas, books, and perspectives.",
        icon: "Sparkles",
        color: "#FF7043",
      },
      {
        weekNumber: 6,
        title: "Career Growth",
        description: "Advance your professional goals and clarify your career vision.",
        icon: "Briefcase",
        color: "#FFD700",
      },
      {
        weekNumber: 7,
        title: "Relationships",
        description: "Strengthen connections and build meaningful relationships.",
        icon: "Heart",
        color: "#4DD0E1",
      },
      {
        weekNumber: 8,
        title: "Integration & Celebration",
        description: "Integrate all learnings and celebrate your transformation.",
        icon: "Trophy",
        color: "#FFD700",
      },
    ];

    for (const week of weekData) {
      await db.insert(schema.weeks).values(week).onConflictDoNothing();
    }

    console.log(" 8 weeks created");

    // Create sample challenges for week 1
    const [week1] = await db.select().from(schema.weeks).where(eq(schema.weeks.weekNumber, 1));

    if (week1 && admin) {
      const challengeData = [
        {
          weekId: week1.id,
          title: "Complete your self-assessment",
          description: "Take 30 minutes to honestly evaluate your current state across all life areas.",
          type: "daily",
          createdBy: admin.id,
        },
        {
          weekId: week1.id,
          title: "Define your core values",
          description: "List your top 5 core values that will guide your transformation journey.",
          type: "daily",
          createdBy: admin.id,
        },
        {
          weekId: week1.id,
          title: "Set 8-week goals",
          description: "Write down 3-5 specific, measurable goals you want to achieve by the end of the program.",
          type: "weekly",
          createdBy: admin.id,
        },
      ];

      for (const challenge of challengeData) {
        await db.insert(schema.challenges).values(challenge).onConflictDoNothing();
      }

      console.log(" Sample challenges created for Week 1");
    }

    // Create sample resources
    const resourceData = [
      {
        title: "Atomic Habits by James Clear",
        description: "Learn how tiny changes can lead to remarkable results in building better habits.",
        category: "Self-Discipline",
        type: "article",
        url: "https://jamesclear.com/atomic-habits",
        createdBy: admin?.id || "",
      },
      {
        title: "The Power of Now by Eckhart Tolle",
        description: "A guide to spiritual enlightenment and living in the present moment.",
        category: "Mindfulness",
        type: "article",
        url: "https://www.eckharttolle.com/the-power-of-now/",
        createdBy: admin?.id || "",
      },
      {
        title: "Fitness Fundamentals",
        description: "A comprehensive guide to building a sustainable exercise routine.",
        category: "Health",
        type: "video",
        url: "https://www.youtube.com/watch?v=example",
        createdBy: admin?.id || "",
      },
    ];

    if (admin) {
      for (const resource of resourceData) {
        await db.insert(schema.resources).values(resource).onConflictDoNothing();
      }
      console.log(" Sample resources created");
    }

    // Create welcome announcement
    if (admin) {
      await db.insert(schema.announcements).values({
        title: "Welcome to Arcane Arc! ",
        content: "Your transformation journey begins today. Remember to journal daily, complete your weekly challenges, and connect with the community. We're here to support you every step of the way!",
        createdBy: admin.id,
      }).onConflictDoNothing();

      console.log(" Welcome announcement created");
    }

    console.log(" Database seeding completed successfully!");
  } catch (error) {
    console.error(" Error seeding database:", error);
    throw error;
  }
}

seed()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error(error);
    process.exit(1);
  });

client/src/components/ui/button.tsx.content:
import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

shared/schema.ts.content:
import { sql, relations } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, boolean, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  fullName: text("full_name").notNull(),
  role: text("role").notNull().default("user"), // "user" or "admin"
  createdAt: timestamp("created_at").defaultNow().notNull(),
  currentWeek: integer("current_week").default(1).notNull(), // 1-8
  streak: integer("streak").default(0).notNull(),
  totalJournals: integer("total_journals").default(0).notNull(),
});

// Program weeks
export const weeks = pgTable("weeks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  weekNumber: integer("week_number").notNull().unique(), // 1-8
  title: text("title").notNull(), // e.g., "Self Reflection"
  description: text("description").notNull(),
  icon: text("icon").notNull(), // lucide icon name
  color: text("color").notNull(), // hex color for theme
});

// Challenges
export const challenges = pgTable("challenges", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  weekId: varchar("week_id").references(() => weeks.id).notNull(),
  title: text("title").notNull(),
  description: text("description").notNull(),
  type: text("type").notNull(), // "daily" or "weekly"
  createdAt: timestamp("created_at").defaultNow().notNull(),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
});

// User progress on challenges
export const userChallenges = pgTable("user_challenges", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  challengeId: varchar("challenge_id").references(() => challenges.id).notNull(),
  completed: boolean("completed").default(false).notNull(),
  completedAt: timestamp("completed_at"),
});

// Journal entries
export const journals = pgTable("journals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  date: timestamp("date").defaultNow().notNull(),
  achievement: text("achievement"),
  challenge: text("challenge"),
  gratitude: text("gratitude"),
  mood: text("mood"), // "great", "good", "okay", "challenging"
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Learning resources
export const resources = pgTable("resources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  category: text("category").notNull(), // "Self-Discipline", "Health", "Business", "Mindfulness"
  type: text("type").notNull(), // "video", "pdf", "article"
  url: text("url").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
});

// Announcements
export const announcements = pgTable("announcements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  content: text("content").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  createdBy: varchar("created_by").references(() => users.id).notNull(),
});

// Achievements/Badges
export const achievements = pgTable("achievements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description").notNull(),
  icon: text("icon").notNull(), // lucide icon name
  criteria: jsonb("criteria").notNull(), // { type: "journals", count: 7 }
});

// User achievements
export const userAchievements = pgTable("user_achievements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  achievementId: varchar("achievement_id").references(() => achievements.id).notNull(),
  unlockedAt: timestamp("unlocked_at").defaultNow().notNull(),
});

// User progress tracking
export const userProgress = pgTable("user_progress", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  weekId: varchar("week_id").references(() => weeks.id).notNull(),
  completed: boolean("completed").default(false).notNull(),
  completedAt: timestamp("completed_at"),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  journals: many(journals),
  userChallenges: many(userChallenges),
  userAchievements: many(userAchievements),
  userProgress: many(userProgress),
  createdChallenges: many(challenges),
  createdResources: many(resources),
  createdAnnouncements: many(announcements),
}));

export const weeksRelations = relations(weeks, ({ many }) => ({
  challenges: many(challenges),
  userProgress: many(userProgress),
}));

export const challengesRelations = relations(challenges, ({ one, many }) => ({
  week: one(weeks, {
    fields: [challenges.weekId],
    references: [weeks.id],
  }),
  createdBy: one(users, {
    fields: [challenges.createdBy],
    references: [users.id],
  }),
  userChallenges: many(userChallenges),
}));

export const journalsRelations = relations(journals, ({ one }) => ({
  user: one(users, {
    fields: [journals.userId],
    references: [users.id],
  }),
}));

export const resourcesRelations = relations(resources, ({ one }) => ({
  createdBy: one(users, {
    fields: [resources.createdBy],
    references: [users.id],
  }),
}));

export const announcementsRelations = relations(announcements, ({ one }) => ({
  createdBy: one(users, {
    fields: [announcements.createdBy],
    references: [users.id],
  }),
}));

export const achievementsRelations = relations(achievements, ({ many }) => ({
  userAchievements: many(userAchievements),
}));

export const userChallengesRelations = relations(userChallenges, ({ one }) => ({
  user: one(users, {
    fields: [userChallenges.userId],
    references: [users.id],
  }),
  challenge: one(challenges, {
    fields: [userChallenges.challengeId],
    references: [challenges.id],
  }),
}));

export const userAchievementsRelations = relations(userAchievements, ({ one }) => ({
  user: one(users, {
    fields: [userAchievements.userId],
    references: [users.id],
  }),
  achievement: one(achievements, {
    fields: [userAchievements.achievementId],
    references: [achievements.id],
  }),
}));

export const userProgressRelations = relations(userProgress, ({ one }) => ({
  user: one(users, {
    fields: [userProgress.userId],
    references: [users.id],
  }),
  week: one(weeks, {
    fields: [userProgress.weekId],
    references: [weeks.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  email: true,
  password: true,
  fullName: true,
});

export const insertWeekSchema = createInsertSchema(weeks).omit({
  id: true,
});

export const insertChallengeSchema = createInsertSchema(challenges).omit({
  id: true,
  createdAt: true,
});

export const insertJournalSchema = createInsertSchema(journals).omit({
  id: true,
  createdAt: true,
  date: true,
});

export const insertResourceSchema = createInsertSchema(resources).omit({
  id: true,
  createdAt: true,
});

export const insertAnnouncementSchema = createInsertSchema(announcements).omit({
  id: true,
  createdAt: true,
});

export const insertAchievementSchema = createInsertSchema(achievements).omit({
  id: true,
});

export const insertUserChallengeSchema = createInsertSchema(userChallenges).omit({
  id: true,
  completedAt: true,
});

export const insertUserProgressSchema = createInsertSchema(userProgress).omit({
  id: true,
  completedAt: true,
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Week = typeof weeks.$inferSelect;
export type InsertWeek = z.infer<typeof insertWeekSchema>;
export type Challenge = typeof challenges.$inferSelect;
export type InsertChallenge = z.infer<typeof insertChallengeSchema>;
export type Journal = typeof journals.$inferSelect;
export type InsertJournal = z.infer<typeof insertJournalSchema>;
export type Resource = typeof resources.$inferSelect;
export type InsertResource = z.infer<typeof insertResourceSchema>;
export type Announcement = typeof announcements.$inferSelect;
export type InsertAnnouncement = z.infer<typeof insertAnnouncementSchema>;
export type Achievement = typeof achievements.$inferSelect;
export type InsertAchievement = z.infer<typeof insertAchievementSchema>;
export type UserChallenge = typeof userChallenges.$inferSelect;
export type InsertUserChallenge = z.infer<typeof insertUserChallengeSchema>;
export type UserProgress = typeof userProgress.$inferSelect;
export type InsertUserProgress = z.infer<typeof insertUserProgressSchema>;
export type UserAchievement = typeof userAchievements.$inferSelect;

